        desc:"It is a web application that will help the general users to find the right kind of manpower they need to do household related tasks like construction and maintenance. It also allows the administrator to upload the information regarding the available manpower",

        title: "Rampa UI Design",
        img:"",
      },
      {
        id: "3",
        icon:"./assets/react.png",
        desc:"hello3",

        title: "E-commerce Web Design",
        img:"",
      },
    
  ];
  const [currentSlide,setCurrentSlide]=useState(0);
  

  const handleClick=(way)=>{
    way==="left" ? setCurrentSlide(currentSlide> 0 ? currentSlide-1:2):
    setCurrentSlide(currentSlide<data.length-1 ? currentSlide+1:0 );
  };
    return (
      <div className="work" id="work">
        <div 
        className="slider"
        style={{ transform: `translateX(-${currentSlide * 100}vw)` }}>
          { data.map((d) => (
          <div className="container">
            <div className="item">
              <div className="left">
                <div className="leftContainer">
                  <div className="imgContainer">
                    <img src={d.img} alt=""></img>
                  </div>
                  <h2>{d.title}</h2>
                  <p>{d.desc}</p>
                  {/* <span>It is a web application that will help the general users to find the right kind of manpower they need to do household related tasks like construction and maintenance. It also allows the administrator to upload the information regarding the available manpower</span> */}
                </div>
              </div>
              <div className="right">
              {/* <img src="assets/uml.png" alt="aaaaaa"/> */}
                {/* <span>It is a web application that will help the general users to find the right kind of manpower they need to do household related tasks like construction and maintenance. It also allows the administrator to upload the information regarding the available manpower</span> */}

              </div>
            </div>
            </div>
            )
            )}
          </div>
        
     
        
 <img src="assets/rightarrow.png" className="arrow left"alt="hello"onClick={()=>handleClick("left")}></img>
 <img src="assets/rightarrow.png" className="arrow right"onClick={()=>handleClick("")}></img>
  </div>


