import React, { useState } from "react";
import "./aboutme.scss"


export default function Aboutme() {
  const data=[
    
      {
        id: "1",
        icon:"./assets/uml.png",
        title: "My UML",
        desc:"hello1",
        img:
          "",
      },
      {
        id: "2",
        icon:"./assets/java.png",
        desc:"hello2",

        title: "Rampa UI Design",
        img:"",
      },
      {
        id: "3",
        icon:"./assets/react.png",
        desc:"hello3",

        title: "E-commerce Web Design",
        img:"",
      },
    
  ];
  const [currentSlide,setCurrentSlide]=useState(0);
  

  const handleClick=(way)=>{
    way==="left" ? setCurrentSlide(currentSlide> 0 ? currentSlide-1:2):
    setCurrentSlide(currentSlide<data.length-1 ? currentSlide+1:0 );
  };
    return (
      <div className="aboutme" id="aboutme">
        <div 
        className="slider"
        style={{ transform: `translateX(-${currentSlide * 100}vw)` }}>
          { data.map((d) => (
          <div className="container">
            <div className="item">
              <div className="left">
                <div className="leftContainer">
                  <div className="imgContainer">
                    {/* <img src={d.img} alt=""></img> */}
                  </div>
                  <h2>{d.title}</h2>
                  <p>{d.desc}</p>
                  <span>Projects</span>
                </div>
              </div>
              <div className="right">
              {/* <img src="assets/uml.png" alt="aaaaaa"/> */}
                 <h1>An ambitious communicator with major aspirations to be a skilled software architect, worked on web and mobile application projects</h1>

              </div>
            </div>
            </div>
            )
            )}
          </div>
        
     
        
 <img src="assets/rightarrow.png" className="arrow left"alt="hello"onClick={()=>handleClick("left")}></img>
 <img src="assets/rightarrow.png" className="arrow right"onClick={()=>handleClick("")}></img>
  </div>


    );
  }
